mapper.update:
UPDATE member SET
	mmTeacherNy = #{mmTeacherNy}
	,mmGradeCd = #{mmGradeCd}
	,mmName = #{mmName}
	,mmId = #{mmId}
	,mmPassword = #{mmPassword}
	,mmPhoneNumber = #{mmPhoneNumber}
	,mmDob = #{mmDob}
	,mmMemberEmail = #{mmMemberEmail}
	,mmDesc = #{mmDesc}
	,mmEventNotificationNy = #{mmEventNotificationNy}
	,mmAlarmInvitationNy = #{mmAlarmInvitationNy}
	,mmAlarmReplyNy = #{mmAlarmReplyNy}
	,modDateTime = #{modDateTime}
	,modDateTimeSvr = #{modDateTimeSvr}
WHERE 1=1
	AND mmSeq=#{mmSeq}

UPDATE ${tableName} SET
	type = #{type}
	,defaultNy = #{defaultNy}
	,sort = #{sort}
	,originalName = #{originalName}
	,uuidName = #{uuidName}
	,ext = #{ext}
	,size = #{size}
	,path = #{path}
	,delNy = #{delNy}
WHERE 1=1
	AND pseq = #{pseq}
	AND type = #{type}

